function doGet(e) {
  var action = e.parameter.action;

  // Handle Read Action (Poll Data)
  // Expects a 'date' parameter (YYYY-MM-DD) to read from that specific sheet
  if (action == 'read') {
    var dateParam = e.parameter.date;
    // ID: 1Ccff_Xn7jLJtP31n3ZiFlzV9LavdJLgkxB968j9oJyg
    var doc = SpreadsheetApp.openById("1Ccff_Xn7jLJtP31n3ZiFlzV9LavdJLgkxB968j9oJyg");

    // Default to the first sheet if no date is provided, otherwise find the date sheet
    var sheet;
    if (dateParam) {
      sheet = doc.getSheetByName(dateParam);
    } else {
      sheet = doc.getSheets()[0];
    }

    // If sheet doesn't exist (e.g. no attendance today yet), return empty list
    if (!sheet) return ContentService.createTextOutput("[]").setMimeType(ContentService.MimeType.JSON);

    // Get all data
    var lastRow = sheet.getLastRow();
    if (lastRow < 2) return ContentService.createTextOutput("[]").setMimeType(ContentService.MimeType.JSON);

    var rows = sheet.getRange(2, 1, lastRow - 1, 5).getValues();
    var data = rows.map(function(r) {
      return {
        timestamp: r[0],
        date: r[1],
        name: r[2],
        studentId: r[3],
        email: r[4]
      };
    });

    return ContentService.createTextOutput(JSON.stringify(data)).setMimeType(ContentService.MimeType.JSON);
  }

  return ContentService.createTextOutput("Connection success! Cloud Storage is Active.");
}

function doPost(e) {
  if (typeof e === 'undefined') {
    return ContentService.createTextOutput("Error: Run as Web App.");
  }

  var action = e.parameter.action;

  // ID: 1Ccff_Xn7jLJtP31n3ZiFlzV9LavdJLgkxB968j9oJyg
  var doc = SpreadsheetApp.openById("1Ccff_Xn7jLJtP31n3ZiFlzV9LavdJLgkxB968j9oJyg");

  // Parse Data to get the date
  var data = {};
  try {
    if (e.postData && e.postData.contents) {
        data = JSON.parse(e.postData.contents);
    }
  } catch(err) {}

  // Determine which sheet to use (based on Date or default)
  var sheetName = data.date || e.parameter.date;
  var sheet;

  if (sheetName) {
      sheet = doc.getSheetByName(sheetName);
      if (!sheet && action !== 'clear') {
          // Create new sheet if it doesn't exist (only if we are not clearing)
          sheet = doc.insertSheet(sheetName);
          sheet.appendRow(["Timestamp", "Date", "Full Name", "Student ID", "Email"]);
      }
  } else {
      sheet = doc.getSheets()[0];
  }

  // Handle Clear Action
  if (action == 'clear') {
    if (sheet) {
        var lastRow = sheet.getLastRow();
        if (lastRow >= 2) {
            sheet.getRange(2, 1, lastRow - 1, 5).clearContent();
        }
    }
    return ContentService
      .createTextOutput(JSON.stringify({ "result": "success" }))
      .setMimeType(ContentService.MimeType.JSON);
  }

  try {
    // Double check sheet exists before writing
    if (!sheet) {
         // Should have been created above, but fallback
         sheet = doc.insertSheet(sheetName || "Attendance");
         sheet.appendRow(["Timestamp", "Date", "Full Name", "Student ID", "Email"]);
    }

    if (sheet.getLastRow() === 0) {
      sheet.appendRow(["Timestamp", "Date", "Full Name", "Student ID", "Email"]);
    }

    sheet.appendRow([
      data.timestamp,
