// --- CONFIGURATION ---
// The sheet name to use for the Master List
var SHEET_NAME = "Attendance Master";

function doGet(e) {
  var action = e.parameter.action;
  // ID: 1Ccff_Xn7jLJtP31n3ZiFlzV9LavdJLgkxB968j9oJyg
  var doc = SpreadsheetApp.openById("1Ccff_Xn7jLJtP31n3ZiFlzV9LavdJLgkxB968j9oJyg");
  var sheet = doc.getSheetByName(SHEET_NAME);

  // If sheet doesn't exist yet, return empty
  if (!sheet) return ContentService.createTextOutput("[]").setMimeType(ContentService.MimeType.JSON);

  if (action == 'read') {
    // Read the Master Sheet
    // Structure: Col 1=ID, Col 2=Name, Col 3=Email, Col 4+=Dates
    var lastRow = sheet.getLastRow();
    if (lastRow < 2) return ContentService.createTextOutput("[]").setMimeType(ContentService.MimeType.JSON);

    var lastCol = sheet.getLastColumn();
    // Get Data Range (excluding headers)
    var data = sheet.getRange(2, 1, lastRow - 1, lastCol).getValues();
    var headers = sheet.getRange(1, 1, 1, lastCol).getValues()[0];

    // Find column index for TODAY (e.g. from parameter or just find latest?)
    // For the app view, we want to show today's status.
    var dateParam = e.parameter.date; 
    var dateColIndex = -1;

    if (dateParam) {
      dateColIndex = headers.indexOf(dateParam);
    }

    // Transform into App format
    var output = [];
    for (var i = 0; i < data.length; i++) {
       var row = data[i];
       // If we found a column for today, check if present
       // If today is not in sheet yet, no one is "present" in the cloud sense
       var isPresent = false;
       var timestamp = Date.now(); // Dummy timestamp for sorting if existing

       if (dateColIndex > -1) {
          var val = row[dateColIndex];
          if (val && val !== "" && val !== "ABSENT") {
             isPresent = true;
          }
       }

       if (isPresent) {
         output.push({
           studentId: row[0],
           name: row[1],
           email: row[2],
           date: dateParam || "",
           timestamp: timestamp
         });
       }
    }

    return ContentService.createTextOutput(JSON.stringify(output)).setMimeType(ContentService.MimeType.JSON);
  }

  return ContentService.createTextOutput("Connection success!");
}

function doPost(e) {
  if (typeof e === 'undefined') return ContentService.createTextOutput("Error");

  // ID: 1Ccff_Xn7jLJtP31n3ZiFlzV9LavdJLgkxB968j9oJyg
  var doc = SpreadsheetApp.openById("1Ccff_Xn7jLJtP31n3ZiFlzV9LavdJLgkxB968j9oJyg");
  var sheet = doc.getSheetByName(SHEET_NAME);

  // Create Sheet if missing
  if (!sheet) {
    sheet = doc.insertSheet(SHEET_NAME);
    // PDF Style Headers
    sheet.getRange(1, 1, 1, 3).setValues([["Student ID", "Name", "Email"]]);
    sheet.setFrozenRows(1);
    sheet.setFrozenColumns(1);
  }

  var action = e.parameter.action;
  var data = {};
  try {
     if (e.postData) data = JSON.parse(e.postData.contents);
  } catch(err){}

  var todayDate = data.date || e.parameter.date;
  if (!todayDate) return ContentService.createTextOutput(JSON.stringify({"result":"error", "message":"No date provided"}));

  // 1. Find or Create Column for Today
  var lastCol = sheet.getLastColumn();
  var headers = sheet.getRange(1, 1, 1, lastCol).getValues()[0];
  var dateColIndex = headers.indexOf(todayDate); // 0-based index

  if (dateColIndex === -1) {
    // New Date Column
    dateColIndex = lastCol; // The new column index
    sheet.getRange(1, dateColIndex + 1).setValue(todayDate);
    // Format Header
    sheet.getRange(1, dateColIndex + 1).setFontWeight("bold").setBorder(true, true, true, true, true, true);
  }

  var realColIndex = dateColIndex + 1; // 1-based for API

  // ACTION: MARK ATTENDANCE
  if (!action || action !== 'finalize') {
     var studentId = data.studentId;
     var name = data.name;
     var email = data.email;

     // Find Student Row
     var lastRow = sheet.getLastRow();
     var rowIndex = -1;

     if (lastRow > 1) {
       var ids = sheet.getRange(2, 1, lastRow - 1, 1).getValues().flat();
       var idx = ids.indexOf(studentId);
       if (idx > -1) rowIndex = idx + 2; // +2 because headers=1 and index=0-based
     }

     // Create Student Row if missing
     if (rowIndex === -1) {
       sheet.appendRow([studentId, name, email]);
       rowIndex = sheet.getLastRow();
     }

     // Mark Present
     sheet.getRange(rowIndex, realColIndex).setValue("PRESENT").setFontColor("green");

     return ContentService.createTextOutput(JSON.stringify({"result":"success"}));
  }

  // ACTION: FINALIZE (Mark Absent & Sort)
  if (action === 'finalize') {
     var lastRow = sheet.getLastRow();
     if (lastRow < 2) return ContentService.createTextOutput(JSON.stringify({"result":"success"}));

